import * as React from "react";
import type { SVGIconProps } from "../../svgIconProps";
import { IconSize } from "../../iconTypes";
import { SVGIconContainer } from "../../svgIconContainer";

export const Record: React.FC<SVGIconProps> = React.forwardRef<any, SVGIconProps>((props, ref) => {
    const isLarge = props.size! >= IconSize.LARGE;
    const pixelGridSize = isLarge ? IconSize.LARGE : IconSize.STANDARD;
    const translation = `${-1 * pixelGridSize / 0.05 / 2}`;
    const style = { transformOrigin: "center" };
    return (
        <SVGIconContainer iconName="record" ref={ref}  {...props}>
            <path
                d={isLarge ? "M60 200A140 140 0 0 1 340 200A140 140 0 0 1 60 200" : "M60 160A100 100 0 0 1 260 160A100 100 0 0 1 60 160"}
                fillRule="evenodd"
                transform={`scale(0.05, -0.05) translate(${translation}, ${translation})`}
                style={style}
            />
        </SVGIconContainer>
   );
});
Record.defaultProps = {
    size: IconSize.STANDARD,
};
Record.displayName = `Blueprint5.Icon.Record`;
export default Record;
